var linkCart=document.querySelector(".product-buy"),popupCart=document.querySelector(".popup-cart"),closeCart=popupCart.querySelector(".popup-close");linkCart.addEventListener("click",function(p){p.preventDefault(),popupCart.classList.add("popup-show")}),closeCart.addEventListener("click",function(p){p.preventDefault(),popupCart.classList.remove("popup-show")}),window.addEventListener("keydown",function(p){27===p.keyCode&&(p.preventDefault(),popupCart.classList.contains("popup-show")&&(popupCart.classList.remove("popup-show"),popupCart.classList.remove("popup-error")))});